      setDisplayStudent(function (display) {
        if (display) {
          setStuCode(function () {
            socket.emit("onLecChange", value, roomId);
            return value;
          });
        }

        return display;
      });
    }); // todo-kw: revisit this function - may be useless given current logic

    socket.on("no student", function (msg) {
      socket.sid = "";
      setSid("");
      socket.on = true;
      setStuCode("");
    }); // for file downloading (via fb):

