      usersFileDirIsEmpty().then(function (res) {
        if (res) {
          var cp = "/files/defaults/yteacher.txt";
          downloadFile(cp).then(function (res) {
            var f = new File([res.code], "whateveryouwant.txt");
            return uploadFile(f);
          }).then(function (res) {
            res.file.text().then(function (code) {
              setCode(code);
              setCodePath(res.codePath);
              setCodeFilename(res.file.name);
            });
          });
        } else {
          getOnlyFilesName().then(function (res) {
            downloadFile(res.codePath).then(function (res2) {
              setCode(res2.code);
              setCodePath(res.codePath);
              setCodeFilename(res.fileName);
            });
          });
        }
      });
